        };

        // fetch(
        //     this.state.url + apiUrl, options)
        //     .then(res => res.json())
        //     .then(result => {
        //         this.setState({
        //             response: result,
        //             isAddProduct: false,
        //             isEditProduct: false
        //         })
        //     },
        //         (error) => {
        //             this.setState({ error });
        //         }
        //     )
    }

    editProduct = productId => {
        var data = {
            table: 'products',
            where: {
                field: 'id = ?',
                value: [productId]
            }
        };
        const options = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(data)
        };
        fetch(this.state.url + 'select-one', options)
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result);
                    this.setState({
                        product: result,
                        isEditProduct: true,
                        isAddProduct: true
                    });
                },
                (error) => {
                    this.setState({ error });
                }
            )
    }

    render() {

        let productForm;
        if (this.state.isAddProduct || this.state.isEditProduct) {
            productForm = <AddProduct onFormSubmit={this.onFormSubmit} product={this.state.product} />
        }

        return (
            <div className="App">
                <Container>
                    <h1 style={{ textAlign: 'center' }}>React Tutorial</h1>
                    {!this.state.isAddProduct && <Button variant="primary" onClick={() => this.onCreate()}>Add Product</Button>}
                    {this.state.response.status === 'success' && <div><br /><Alert variant="info">{this.state.response.message}</Alert></div>}
                    {!this.state.isAddProduct && <ProductList editProduct={this.editProduct} />}
                    {productForm}
                    {this.state.error && <div>Error: {this.state.error.message}</div>}
                </Container>
            </div>
        );
    }
}

export default App;
